<template>
    <transition name="fade" @after-leave="onAfterLeave">
        <div v-show="isShow">
            <div class="container-toast container-toast__loading" v-if="type == 'loading'">
                <span class="loading"></span>
                <span class="text">{{ title }}</span>
            </div>
            <div
                class="container-toast container-toast__box"
                :class="`container-toast__${position}`"
                v-else
                v-html="title"
            ></div>
        </div>
    </transition>
</template>
<script setup lang="ts">
import { ref } from 'vue';
const props = defineProps({
    type: {
        type: String,
        default: '',
    },
    title: {
        type: String,
        default: '',
    },
    position: {
        type: String,
        default: 'middle',
    },
    duration: {
        type: Number,
        default: 1500,
    },
    close: {
        type: Function,
        default() {
            return {};
        },
    },
});
const isShow = ref<boolean>(false);
const show = () => {
    isShow.value = true;
    if (props.duration > 0 && props.type != 'loading') {
        setTimeout(() => {
            isShow.value = false;
        }, props.duration);
    }
};
const hide = () => {
    isShow.value = false;
};
const onAfterLeave = () => {
    props.close();
};
defineExpose({
    show,
    hide,
});
</script>
